import csvimport statistics# Path to collect data from the Resources folderbudget_data = '/Users/madhuvenkidusamy/Documents/Data Science Bootcamp/Homeworks/python-challenge/PyBank/Resources/budget_data.csv'months = []prof_loss = []# Read in the CSV filewith open(budget_data) as csvfile:    csvreader = csv.reader(csvfile)        next(csvreader) #this line skips the header        #read the columns of the csv as lists    for row in csvreader:        months.append(row[0])         prof_loss.append(int(row[1])) #gives list of strings, need to convert to integers        total_months = len(months) # gives number of months in datasetnet_profit = sum(prof_loss) # gives net profit or loss# create a new list of changes in profits change = [prof_loss[i+1]-prof_loss[i] for i in range(len(prof_loss)-1)]# find average changeavg_change = round(statistics.mean(change),2) # round to 2 decimal places# initialize max and min profit delta variables# this will be populated with the indices in loop belowinc = 0dec = 0# use loop to find index of the max and min profit deltafor i in range(len(change)-1):    if change[i] == max(change):        inc = i    elif change[i] == min(change):        dec = i# gives dates of lowest and highest profit change        inc_month = months[inc+1]dec_month = months[dec+1]# print analysisprint('\nFinancial Analysis\n-------------------')print('Total Months: ' + str(total_months))print('Total : $' + str(net_profit))print('Average Change: $' + str(avg_change))print('Greatest Increase in Profits: ' + inc_month + " ($" + str(max(change)) + ")" )print('Greatest Decrease in Profits: ' + dec_month + " ($" + str(min(change)) + ")" )            